userDefinedLinkHintCss:
div > .vimiumHintMarker {
  /* linkhint boxes */
  background: -webkit-gradient(
    linear,
    left top
    left bottom, 
    color-stop(0%,#FFF785),
    color-stop(100%,#FFC542));
  border: 1px solid #E3BE23;
  padding: 2px 2px 4px 2px;
}

div > .vimiumHintMarker span {
  /* linkhint text */
  color: black;
  font-family: sans;
  font-weight: bold;
  font-size: 16px;
}

div > .vimiumHintMarker > .matchingCharacter {
}
